    var luong1Ngay = +document.getElementById('inputNum1').value;
    var soNgayLam = +document.getElementById('inputNum2').value;
    var tienLuong = luong1Ngay * soNgayLam;
    document.getElementById('txtSalary').innerHTML = 'Ti·ªÅn l∆∞∆°ng c·ªßa b·∫°n l√†: ' + tienLuong.toLocaleString('it-IT', { style: 'currency', currency: 'VND' });
