//Bai 4
// input: chieu dai va chieu rong
// process:
// dien tich=dai*rong
// chuvi=(dai+rong)*2
// output:
// dien tich
// chu vi
document.getElementById('btnCal').onclick = function () {
    var chieuDai = +document.getElementById('width').value;
    var chieuRong = +document.getElementById('height').value;
    var dienTich = chieuDai * chieuRong;
    var chuVi = (chieuDai + chieuRong) * 2;
    document.getElementById('txtCal').innerHTML =
        "Di·ªán t√≠ch c·ªßa h√¨nh ch·ªØ nh·∫≠t l√†: " + dienTich +
        '<br>üëâ' + "Chu vi c·ªßa h√¨nh ch·ªØ nh·∫≠t l√†: " + chuVi;
}

//Bai 5
// input: so co 2 chu so
// process: 
// lay tung so tu hang don vi den hang chuc
// luu 2 so vao 2 bien hang chuc va hang don vi
// tinh tong hai so vua lay ra
// output:
// tong cua 2 ki so trong so vua nhap
document.getElementById('btnSum').onclick = function () {
    var soCo2ChuSo = +document.getElementById('number').value;
    var soHangDonVi=soCo2ChuSo%10;
    var soHangChuc=soCo2ChuSo/10;
    var tongCua2KiSo=Math.floor(soHangChuc)+soHangDonVi;
    document.getElementById('txtSum').innerHTML = "T·ªïng c·ªßa hai k√≠ s·ªë l√†: "+tongCua2KiSo;
}
